import {Card, Button, Popover } from "antd";
import React from "react";
import { RetweetOutlined } from "@ant-design";
const PostCard=({post})=>{
    return(
        <div>
            <Card
                cover={post.Images[0]&&<PostImages images={post.Images}/>}
                actions={[
                    <RetweetOutlined/>,
                    <HeartOutlined/>,
                    <MessageOutlined/>,
                    <Popover>
                        <EllipsisOutlined/>
                    </Popover>
                ]}
            >
                <Image/>
                <Content/>
            </Card>
            <CommentForm/>
            <Comments/>
        </div>
    )
}

export default PostCard;
