import {all,fork,take} from 'redux-saga/effects';
/**
 * all, fork, take : saga의 effect
 * 
 */

function* watchLogin(){
    yield take('LOG_IN');
}

function* watchLogOut(){
    yield take('LOG_OUT');
}

// rootSaga에 비동기 액션들을 넣어준다.
export default function* rootSaga(){
    yield all([
        fork(watchLogin),
        fork(watchLogOut),
        fork(watchAddPost)
    ])
}
