import {all,fork,take} from 'redux-saga/effects';
import axios from 'axios';
/**
 * all, call ,fork, take : saga의 effect
 * fork(param) : param으로 받은 함수를 실행
 * ※fork 와 call 은 엄연히 다르다!!
 */

//logInAPI 함수 호출. generator이 아님. 
function logInAPI(){
    //서버에 요청전홍
    return axios.post('/api/login')
}

function* logIn(){
    const result = yield call(logInAPI);//logInAPI 함수의 반환갑을 받는다.
}

function* watchLogin(){
    yield take('LOG_IN',logIn);
    //LOG_IN action 이 실행 될 때 까지 기다리겠다.
}

function* watchLogOut(){
    yield take('LOG_OUT');
}

function* watchAddPost(){
    yield take('ADD_POST');
}

// rootSaga에 비동기 액션들을 넣어준다.
export default function* rootSaga(){
    yield all([
        fork(watchLogin),
        fork(watchLogOut),
        fork(watchAddPost),
    ])
}
