export const initialState={
    mainPosts:[{
        id:1,
        User:{
            id:1,
            nickname:'dntjr9214'
        },
        content:'첫 번쨰 게시글 #헤시태그 #익스프레스',
        Images:[
            {src:"https://thumb.mt.co.kr/06/2021/01/2021012508208231071_1.jpg/dims/optimize/"},
            {src:"https://thumb.mt.co.kr/06/2021/05/2021052406553082730_1.jpg/dims/optimize/"},
            /* {src:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQGe3EChBupPMd65lXdTEljpfFJj0OCVSfl-VQqgOze5yvgVaG1I07xNB5dUZ1_B05KHlU&usqp=CAU"} */
        ],
        Comments:[{
            User:{
                nickname:'hero',
            },
            content:'우와 개정판이 나왔군요~!'
        },{
            User:{
                nickname:'hero',
            },
            content:'얼른 사고싶어요~',
        }],
    }],
    imagePaths:[],
    postAdded:false,
}

const ADD_POST = 'APP_POST';
export const addPost={
    type:ADD_POST,
}
const dummyPost = {
    id:2,
    content:'더미 데이터 입니다',
    User:{
        id:1,
        nickname:'wooseokKim'
    },
    Images:[],
    Comments:[],
}

const reducer = (state=initialState,action)=>{
    switch(action.type){
        case ADD_POST:
            return {
                ...state,
                mainPosts:[dummyPost,...state.mainPosts],
                postAdded:true
                //게시글의 가장 위쪽으로 추가되기 위하여 push 할 데이터를 앞에 놓는다.
            };
        default :
            return state;
    }
}

export default reducer;
