import {Card, Button } from "antd";
import React from "react";
import {RetweetOutlined} from "@ant-design/icons/lib/icons/RetweetOutlined";
const PostCard=({post})=>{
    return(
        <div>
            <Card
                cover={post.Images[0]&&<PostImages images={post.Images}/>}
                actions={[
                    <RetweetOutlined/>
                ]}
            >
                <Image/>
                <Content/>
            </Card>
            <CommentForm/>
            <Comments/>
        </div>
    )
}

export default PostCard;
