import React,{useState} from 'react'; 
import PropTypes from 'prop-types';
import Slick from 'react-slick';
import styled, { createGlobalStyle } from 'styled-components';
import { CloseOutlined } from '@ant-design/icons';

/**
 * 아래처럼 back tick (``)을 통해
 * javascript 함수를 호출 할 수 있다.
 * 
 * 아래의 예시는 div 라는 함수를
 * back tick(``) 을 통해 호출한 것이다.
 * 
 * 이를 Tagged Template literal 이라고 한다.
*/
const Overlay = styled.div`
    position:fixed;
    z-index:5000;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const Header = styled.header`
    height:44px;
    background:white;
    position:relative;
    padding:0;
    text-align:center;

    &h1{
        margin:0;
        font-size:17px;
        color:#333;
        line-height:44px;
    }

    &button{
        position:absolute;
        right:0;
        top:0;
        padding:15px;
        line-height:14px;
        cursor:pointer;
    }
`;

const SlickWrapper = styled.div`
    height:calc(100%-44px);
    background:#898989;
`;

const ImgWrapper = styled.div`
    padding:32px;
    text-align:center;

    &img{
        margin:0 auto;
        max-height:750px;
    }
`

const Indicator = styled.div`
    text-align:center;

    & > div{
        width:75px;
        height:30px;
        line-height:30px;
        border-radius:15px;
        background:#313131;
        display:inline-block;
        text-align:center;
        color:white;
        font-size:15px;
    }
`

const Global = createGlobalStyle`
    .slick-slide{
        display:inline-block;
    }
    .ant-card-cover{
        transform:none !important;
    }
`;

const CloseBtn=styled(CloseOutlined)`
    position:absolute;
    right:0;
    top:0;
    padding:15px;
    line-height:14px;
    cursor:pointer;
`

const ImagesZoom=({images,onClose})=>{
    const [currentSlide,setCurrentSlide]= useState(0);
    return(
        <Overlay>
            <Global/>
            <Header>
                <h1>상세이미지</h1>
                <CloseBtn onClick={onClose}>X</CloseBtn>
            </Header>
            <div>
                <SlickWrapper>
                    <Slick
                        initialSlide={0}
                        afterChange={(slide)=>setCurrentSlide(slide)}
                        infinite
                        arrows={false}
                        slidesToShow={1}
                        slidesToScroll={1}
                    >
                        {images.map((v,idx)=>(
                            <ImgWrapper key={`${idx}_${v.src}`}>
                                <img key={v.src} src={v.src} alt={v.src}/>
                            </ImgWrapper>
                        ))}
                    </Slick>
                </SlickWrapper>
            </div>
        </Overlay>
    )
}

ImagesZoom.propTypes={
    images:PropTypes.arrayOf(PropTypes.object).isRequired,
    onClose:PropTypes.func.isRequired,
};

export default ImagesZoom;
